mapper.update:
update board set writer=#{writer}, subject=#{subject},
content=#{content}, filename=#{filename}, filesize=#{filesize},
down=#{down} where num=#{num}

mapper.select:
		select *
		from(
    		select A.*, rownum as rn
    		from (
        		select num, writer, subject, reg_date, readcount, filename, filesize, down, re_level, (select count(*) from board_comment where board_num=num) comment_count, show
				from board
				order by ref desc, re_step
    	) A
	)
	where rn between #{start} and #{end}

		select *
		from(
    		select A.*, rownum as rn
    		from (
        		select num, writer, subject, reg_date, readcount, filename, filesize, down, re_level, (select count(*) from board_comment where board_num=num) comment_count, show
				from board


				where ${search_option} like #{keyword}
				and show='y'


				where ( writer like #{keyword}
				or subject like #{keyword}
				or content like #{keyword} )
				and show='y'


		order by ref desc, re_step
    	) A
	)
	where rn between #{start} and #{end}

select count(*)
from board


		where ${search_option} like #{keyword}
		and show='y'


		where ( writer like #{keyword}
		or subject like #{keyword}
		or content like #{keyword} )
		and show='y'
